mapper.select:
SELECT count(*)
FROM tb_user
WHERE userPhone = #{userPhone} and userPassword = #{userPassword}

select userId
from tb_user
where userPhone = #{userPhone} and userPassword = #{userPassword};

SELECT userId
FROM tb_user
WHERE userPhone = #{userPhone};

mapper.update:
UPDATE tb_user
SET userName = #{userName}, userWord = #{userWord}, userSex = #{userSex}
WHERE userId = #{userId};

mapper.select:
SELECT userPhoto, userName, userWord, userSex
FROM tb_user
WHERE userId = #{userId};

select userPhoto, userName, userWord
from tb_user
where userId in (
select userId2
from tb_user_user
where userId = #{userId}
);
